import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
const monday = mondaySdk();

class AppSolution extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {

      },
      context: {},
      name: "",
    };
  }

  componentDidMount() {
    // TODO: set up event listeners
    monday.setToken('eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjE3NjMzMzUyMiwidWlkIjozMzM4NjAzOCwiaWFkIjoiMjAyMi0wOC0xOFQyMjozMzowOS4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTMxNDQ3NTYsInJnbiI6InVzZTEifQ.gai4a2YB1yJhoqJ-mGIX2pBNF91iArRerKqbB6n3u0s');

    monday.listen("settings", (res) => {
      console.log(this.state.settings);
      this.setState({ settings: res.data });
    });

    monday.api(`query { me { name } }`).then((res) => {
      this.setState({ name: res.data.me.name });
    });


    let query = "mutation { create_notification (user_id: 105939, target_id: 674387, text: \"This is a notification\", target_type: project) { text } }";

    fetch ("https://api.monday.com/v2", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization' : 'YOUR_API_KEY_HERE'
      },
      body: JSON.stringify({
        query : query
      })
    })
        .then(res => res.json())
        .then(res => console.log(JSON.stringify(res, null, 2)));
  }

  render() {
    return (
      <div
        className="App"
        style={{ background: this.state.settings.background }}
      >
        Hello, {this.state.name}!
      </div>
    );
  }
}

export default AppSolution;
