import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css"

import {Avatar, Divider, Flex, Heading, Icon, List, ListItem, ListItemIcon} from "monday-ui-react-core";
import BoardList from "./components/board-list/boardList";
import {Add, Board, DropdownChevronDown, Filter} from "monday-ui-react-core/icons";
import classes from "*.module.sass";

const monday = mondaySdk();

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      settings: {},
      name: "",
    };
  }

  componentDidMount() {
    // TODO: set up event listeners
  }

  render() {
    return <div className="app">
      <div >
        <span className={classes["leftpane-workspaces-title"]}>Workspace</span>
        <span className={classes["leftpane-workspaces-options"]}>
        <div className={classes["leftpane-workspaces-description"]}>
        <Avatar size={Avatar.sizes.SMALL} text="W" backgroundColor={Avatar.colors.INDIGO} isSquare />
        Workspace name
        </div>
        <Icon className={classes["leftpane-workspaces-options-icon"]} icon={DropdownChevronDown} />
      </span>
        <List>
          <ListItem>
            <ListItemIcon icon={Add} />
            Add
          </ListItem>
          <ListItem>
            <ListItemIcon icon={Filter} />
            Filter
          </ListItem>
        </List>
        <Divider />
        <List>
          <ListItem>
            <ListItemIcon icon={Board} />
            Board name
          </ListItem>
          <ListItem>
            <ListItemIcon icon={Board} />
            Board name
          </ListItem>
          <ListItem>
            <ListItemIcon icon={Board} />
            Board name
          </ListItem>
        </List>
      </div>
      {/*<Flex direction={Flex.directions.COLUMN}>*/}
      {/*  <Heading type={Heading.types.h1} value="Is my work eco-friendly?"  />*/}
      {/*  <BoardList></BoardList>*/}
      {/*</Flex>*/}

    </div>;
  }
}

export default App;
